import { Home, Truck, AlertTriangle, BarChart, Bell, User, LogOut } from 'lucide-react';

export default function Sidebar() {
  return (
    <div className="bg-red-600 text-white w-64 min-h-screen px-4 py-6 rounded-r-2xl">
      <img src="/assets/taj-logo.png" alt="Taj Gasoline" className="h-12 mb-8 mx-auto" />
      <nav className="space-y-4 text-lg font-semibold">
        <NavItem icon={<Home />} label="Dashboard" active />
        <NavItem icon={<Truck />} label="Trips" />
        <NavItem icon={<AlertTriangle />} label="Exceptions" />
        <NavItem icon={<BarChart />} label="Reports" />
        <NavItem icon={<Bell />} label="Notifications" />
        <NavItem icon={<User />} label="User" />
        <NavItem icon={<LogOut />} label="Logout" />
      </nav>
    </div>
  );
}

function NavItem({ icon, label, active }) {
  return (
    <div className={`flex items-center gap-3 px-4 py-2 rounded-full cursor-pointer ${active ? 'bg-white text-red-600' : 'hover:bg-red-500'}`}>
      <div className="text-xl">{icon}</div>
      <span>{label}</span>
    </div>
  );
}
